section.artwork:
struct {
  uint32 valid_time;
  SignatureScheme dc_cert_verify_algorithm;
  opaque ASN1_subjectPublicKeyInfo<1..2^24-1>;
} Credential;

struct {
  Credential cred;
  SignatureScheme algorithm;
  opaque signature<1..2^16-1>;
} DelegatedCredential;

